import React, { useEffect, useState } from 'react'
import database from './firebase';
import TinderCard from 'react-tinder-card'; 
import './TinderCards.css';

function TinderCards() {

    // useState is basically like declaring an array in REACT.
    const [people, setPeople] = useState([
       /* {
            name: "Doja Cat",
            url: "https://i.ytimg.com/vi/XEJLuJyxLDE/maxresdefault.jpg"
        },
        {
            name: "Saweetie",
            url: "https://miamistandard.news/wp-content/uploads/2022/02/60372/saweetie-has-hella-boyfriends-or-should-we-say-sneaky-links-in-disco-pop-bop-closer-with-her.jpg"

        },
        {
            name: "Yung Miami",
            url: "https://www.billboard.com/wp-content/uploads/media/yung-miami-2019-billboard-1548.jpg"
        } */
    ]);

    // Use Effect is basically a if statement for React. It runs a piece of code based on a condition
    useEffect(() => {
        database.collection('people').onSnapshot(snapshot => (
            setPeople(snapshot.docs.map(doc => doc.data()))
        ))
    }, []);

    // BAD
    // const people = [];
    // people.push("Sahd", "Kobe");

    // GOOD
    // setPeople([...people, "Sahd", "Kobe"]);

  return (
    <div>
        <div className='tinderCards__cardContainer'>
            {people.map((person) => {
                return (
                    <TinderCard 
                    className='swipe' 
                    key={person.name}
                    preventSwipe={["up", "down"]}>
                        <div style={{ backgroundImage: `url(${person.url})` }} className='card'>
                            <h3>{person.name}</h3>
                        </div>
                    </TinderCard>
                )
            })}
        </div>
    </div>
  )
}

export default TinderCards
